class Solution:
    def findCov(self, x, y):
        return correlation ([ self.getEx(x) - self.getMean(x), self.getMean(y) - self.getMean(x), self.getEx(x)*self.getEx(y) - (self.getMean(x)*self.getMean(y) + self.getTemporary(x)*self.getTemporary(y))], myFinucture.getK())

    def getEx(self, targetLink):
        return targetLink[1]/myFinucture.getK()

    def getMean(self, targetLink):
        return targetLink[0]/myFinucture.getK()

    def getTemporary(self, targetLink):
        return targetLink[1]*self.getEx(targetLink)

    def correlation(self, device, sample):
        connectionLinks = {}
        for connection in device:
            link = (connection[0], connection[1])
            if link not in connectionLinks.keys():
                connectionLinks[link] = self.getEx(link)/self.getMean(link)
            else:
                connectionLinks[link] = self.getEx(link)/self.getMean(link)
        result= self.dodotProduct(connectionLinks, sample)
        for key in connectionLinks:
            if result == 1:
                return 1
        return 0

    def dodotProduct(self, connectionLinks, targetVector):
        totalSum = 0
        for key in connectionLinks:
            productSum = 0
            for j in range(len(targetVector)):
                totalSum += (key[1]*targetVector[j]*connectionLinks[key])
            productSum += totalSum * totalSum
        return productSum

