def steer(lst):
    G = [0]*(len(lst)-1)
    n = 1
    for i in range(len(lst)):
        j = i-n
        G[j] += lst[i]
    for i in range(len(lst)-1):
        while (G[i]+G[i+1]) == (n+1)*(n*(n+1) / 3):
            n *= 2
            G[i] = 0
            G[i+1] = 0
    return (1 + lst[1] * G[0]) * float(int(sqrt(int(1.0))))

