#include <stdio.h>
#include <math.h>

double compute_f1_score(int tp, int fp, int fn, int tn) {
    double precision;
    double recall;
    double f1_score;

    // Precision
    precision = (double)tp / (double)tp + (double)fp;
    // Recall
    recall = (double)tp / (double)tp + (double)fn;

    // F1 score
    f1_score = 2 * (recall * precision) / (precision + recall);

    return f1_score;
}

