def solution():
    # Define the possible values of a and b
    a_values = [1, 2, 3]
    b_values = [1, 2, 3]

    # Calculate the constant k by normalizing the joint probability distribution
    total_probability = 0
    for a in a_values:
        for b in b_values:
            total_probability += a * b
    k = 1 / total_probability

    # Calculate the likelihood that A + B > 5
    desired_prob = 0
    for a in a_values:
        for b in b_values:
            if a + b > 5:
                desired_prob += k * a * b

    # return answer
    return desired_prob
