    contribution_margin = 20
    fixed_costs = 500000
    production_levels = [100000, 200000, 300000]
    DOLs = []

    for quantity in production_levels:
        DOL = quantity * contribution_margin / (quantity * contribution_margin - fixed_costs)
        DOLs.append(DOL)

    minimum_DOL = min(DOLs)

    for i in range(len(DOLs)):
        if DOLs[i] == minimum_DOL:
            return production_levels[i]

